// event funtions
void ui_event_ButtonSaveDate(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);

    if(event_code == LV_EVENT_CLICKED) {
        // Get selected date from rollers
        int day = lv_roller_get_selected(ui_RollerDay) + 1;
        int month = lv_roller_get_selected(ui_RollerMonth) + 1;
        int year = lv_roller_get_selected(ui_RollerYear) + 2025;

        // Save to RTC using C++ helper function
        ui_save_date_to_rtc(day, month, year);
        // load time from rtc
        struct tm timeinfo;
        ui_load_datetime_from_rtc(&timeinfo);
        lv_roller_set_selected(ui_RollerHours, timeinfo.tm_hour, LV_ANIM_OFF);
        lv_roller_set_selected(ui_RollerMinutes, timeinfo.tm_min, LV_ANIM_OFF);

        _ui_screen_change(&ui_SetTime, LV_SCR_LOAD_ANIM_FADE_OUT, 250, 0, &ui_WatchFace_screen_init);
    }
}

void ui_event_ButtonDateBack(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);

    if(event_code == LV_EVENT_CLICKED) {
        _ui_screen_change(&ui_WatchFace, LV_SCR_LOAD_ANIM_MOVE_TOP, 250, 0, &ui_WatchFace_screen_init);
    }
}

